#ResultBot instruction: Load before code execution
"""Function CheckPoint pauses thread execution if App.IsPaused() == True 
and exits if App.IsAlive() == False """


import time
import sys

def CheckPoint(timeStep=0.2):
    global App
    if (App.IsAlive() == False):
        sys.exit()
    while (App.IsPaused() == True):
        time.sleep(timeStep)
        if (App.IsAlive() == False):
            sys.exit()

def ExitIfErrorOccurred():
    if (App.GetErrorsString() != ""):
        sys.exit()